import React from 'react';
import SpeakersRenderProps from "./SpeakersRenderProps";

const Speakers = () => {
    return (
        <SpeakersRenderProps>
            {({speakers}) => {
                return (
                    <div>
                        {speakers.map(({imgSrc, name}) => {
                            return <img src={`/images/${imgSrc}`} key={imgSrc} alt={name}/>;
                        })}
                    </div>
                )
            }}
        </SpeakersRenderProps>
    );
}
export default Speakers;
