import React from 'react';
import SpeakersRenderProps from "./SpeakersRenderProps";

const Speakers = ({speakers}) => {
    return (
        <SpeakersRenderProps>
            {({speakers}) => {
                <div>
                    {speakers.map(({imgSrc, name}) => {
                        return <img src={`/images/${imgSrc}`} key={imgSrc} alt={name}/>;
                    })}
                </div>
            }
            }
        </SpeakersRenderProps>

    );
}
const maxSpeakerToShow = 2;
export default withData(maxSpeakerToShow)(Speakers);
