import React from 'react';

export default function Speaker (props) {
    console.log(props);
    return (
        <div className="rounded overflow-hidden shadow-lg p-6" key={props.id}>
            <div className="grid grid-cols-4 mb-6">
                <div className="font-bold text-lg col-span-3">{`${props.firstName} ${props.lastName}`}</div>
                <div className="flex justify-end">
                </div>
            </div>

            <div className="text-gray-600">{props.bio.substr(0, 70) + '...'}</div>
        </div>
    );
 }
